mapper.update:
UPDATE tb_board

SET delete_date = NOW()

WHERE id = #{id}

UPDATE tb_boardComment

SET delete_date = NOW()

WHERE id = #{id}

UPDATE tb_boardComment

SET comment = #{comment},

    update_date = NOW()

WHERE id = #{id}

  AND comment_seq = #{commentSeq}

UPDATE tb_boardComment

SET delete_date = NOW()

WHERE id = #{id}

  AND comment_seq = #{commentSeq}
