mapper.select:
SELECT user_id AS userId , id AS id , password AS password , nickname AS nickName , email AS email , taste AS taste , gender AS gender , birth AS birth , role AS role , create_date AS createDate , update_date AS updateDate , delete_date AS deleteDate FROM tb_user WHERE delete_date IS NULL
AND taste = #{category}
AND (nickname LIKE CONCAT('%', #{keyword}, '%') OR email LIKE CONCAT('%', #{keyword}, '%'))
AND create_date >= #{startDate}
AND create_date <= CONCAT(#{endDate}, ' 23:59:59')
ORDER BY create_date DESC
