mapper.select:
SELECT user_id AS userId , id AS id , password AS password , nickname AS nickName , email AS email , taste AS taste , gender AS gender , birth AS birth , role AS role , create_date AS createDate , update_date AS updateDate , delete_date AS deleteDate FROM tb_user WHERE 1=1
AND taste = #{category}
AND (nickname LIKE CONCAT('%', #{keyword}, '%') OR email LIKE CONCAT('%', #{keyword}, '%'))
AND create_date >= #{startDate}
AND create_date <= CONCAT(#{endDate}, ' 23:59:59')
ORDER BY create_date DESC

mapper.update:
UPDATE tb_user
SET delete_date = NOW()
WHERE user_id = #{userId}

UPDATE tb_user
SET delete_date = NULL
WHERE user_id = #{userId}

mapper.insert:
INSERT INTO tb_notice ( notice_title
                      , notice_content
                      , notice_type
                      , admin_id
                      , id
                      , nickname
                      , notice_createAt
                      , notice_YN)
VALUES ( #{noticeTitle}
       , #{noticeContent}
       , #{noticeType}
       , #{adminId}
       , #{id}
       , #{nickName}
       , NOW(), #{noticeYN})

mapper.update:
UPDATE tb_notice
SET notice_title    = #{noticeTitle}
  , notice_content  = #{noticeContent}
  , notice_type     = #{noticeType}
  , notice_YN       = #{noticeYN}
  , notice_updateAt = NOW()
WHERE notice_id = #{noticeId}
