mapper.update:
UPDATE tb_shop
SET name    = #{name},
    address = #{address},
    content = #{content}
WHERE id = #{id}

mapper.select:
SELECT COALESCE(MAX(img_seq), 0)
FROM tb_shopimg
WHERE id = #{id}

mapper.delete:
DELETE
FROM tb_shopimg
WHERE id = #{id}
  AND img_seq = #{imgSeq}

mapper.update:
UPDATE tb_shopimg
SET main_img = 'N'
WHERE id = #{id}

UPDATE tb_shopimg
SET main_img = 'Y'
WHERE id = #{id}
  AND img_seq = #{imgSeq}
