mapper.select:
SELECT COUNT(*)
FROM tb_user
WHERE id = #{id}

SELECT user_id , id , password , nickname AS nickName , email , taste , gender , birth , role , create_date , update_date , delete_date FROM tb_user WHERE delete_date IS NULL
AND taste = #{category}
AND (nickname LIKE CONCAT('%', #{keyword}, '%') OR email LIKE CONCAT('%', #{keyword}, '%'))
AND create_date >= #{startDate}
AND create_date <= CONCAT(#{endDate}, ' 23:59:59')
ORDER BY create_date DESC
